mapper.select:
SELECT id,goods_name AS goodsName,price, audit_status AS auditStatus,category1_id AS category1Id, category2_id AS category2Id,category3_id AS category3Id FROM tb_goods
seller_id=#{goods.sellerId}
AND goods_name like concat('%',#{goods.goodsName},'%')
AND audit_status=#{goods.auditStatus}
ORDER by id ASC

SELECT id,goods_name AS goodsName,price,category1_id AS category1Id, category2_id AS category2Id,category3_id AS category3Id, audit_status AS auditStatus FROM tb_goods where is_delete IS NULL
AND seller_id=#{goods.sellerId}
AND goods_name like concat('%',#{goodsName},'%')
AND audit_status =#{goods.auditStatus}
ORDER BY id ASC
